    const generateId = () => {
      const existingIds = variables["ecb231ac-e9ca-4a04-a0fe-95c538c51b8b"].map(task => task.id);
      let newId = '';
      let counter = variables["ecb231ac-e9ca-4a04-a0fe-95c538c51b8b"].length + 1;

