// Initialize the canvas for measurements
const setupCanvas = () => {
  const drawingContainer = wwLib.getFrontDocument().getElementById('drawing_canvas_container');
  const measurementOverlay = wwLib.getFrontDocument().getElementById('measurement_overlay');

  if (!drawingContainer || !measurementOverlay) {
    // Elements not ready yet, try again in a moment
    setTimeout(setupCanvas, 100);
    return;
  }

  // Create a canvas element for drawing measurements
  const canvas = wwLib.getFrontDocument().createElement('canvas');
  canvas.id = 'measurement_canvas';
  canvas.style.position = 'absolute';
  canvas.style.top = '0';
  canvas.style.left = '0';
  canvas.style.width = '100%';
  canvas.style.height = '100%';
  canvas.style.pointerEvents = 'none';

  // Add the canvas to the measurement overlay
  measurementOverlay.appendChild(canvas);

  // Set up event listeners for mouse interactions
  drawingContainer.addEventListener('mousedown', handleMouseDown);
  drawingContainer.addEventListener('mousemove', handleMouseMove);
  drawingContainer.addEventListener('mouseup', handleMouseUp);

  // Resize the canvas to match the container
  resizeCanvas();
};

// Function to resize the canvas
const resizeCanvas = () => {
  const canvas = wwLib.getFrontDocument().getElementById('measurement_canvas');
  const container = wwLib.getFrontDocument().getElementById('drawing_canvas_container');

  if (canvas && container) {
    canvas.width = container.offsetWidth;
    canvas.height = container.offsetHeight;

    // Redraw measurements
    drawMeasurements();
  }
};

// Function to handle mouse down events
const handleMouseDown = (event) => {
  // Only proceed if a measurement tool is selected
  const tool = variables['f780e5d4-9fa8-47f5-bcbd-c44c5e28cf51'];
  if (!tool) return;

  // Get the position relative to the container
  const container = wwLib.getFrontDocument().getElementById('drawing_canvas_container');
  const rect = container.getBoundingClientRect();
  const x = event.clientX - rect.left;
  const y = event.clientY - rect.top;

  // Start a new measurement
  utilsFunctions.executeGlobalFunction['e2e109ee-ce66-4131-9480-255d5f36e2a6']({ x, y });

  // Update the cursor based on the selected tool
  updateCursor();
};

// Function to handle mouse move events
const handleMouseMove = (event) => {
  // Only proceed if there's an active measurement
  if (!variables['currentMeasurement'] || !variables['currentMeasurement'].isActive) return;

  // Get the position relative to the container
  const container = wwLib.getFrontDocument().getElementById('drawing_canvas_container');
  const rect = container.getBoundingClientRect();
  const x = event.clientX - rect.left;
  const y = event.clientY - rect.top;

  // Update the measurement
  utilsFunctions.executeGlobalFunction['4ddcd56d-3740-4eb3-916b-50c7fab1f533']({ x, y });

  // Redraw measurements
  drawMeasurements();
};

// Function to handle mouse up events
const handleMouseUp = (event) => {
  // Only proceed if there's an active measurement
  if (!variables['currentMeasurement'] || !variables['currentMeasurement'].isActive) return;

  // Get the position relative to the container
  const container = wwLib.getFrontDocument().getElementById('drawing_canvas_container');
  const rect = container.getBoundingClientRect();
  const x = event.clientX - rect.left;
  const y = event.clientY - rect.top;

  // Complete the measurement
