  drawMeasurements();

  // Reset the cursor
  updateCursor();
};

// Function to draw all measurements on the canvas
const drawMeasurements = () => {
  const canvas = wwLib.getFrontDocument().getElementById('measurement_canvas');
  if (!canvas) return;

  const ctx = canvas.getContext('2d');
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  // Draw the current active measurement if there is one
  if (variables['currentMeasurement'] && variables['currentMeasurement'].isActive) {
    drawMeasurement(ctx, variables['currentMeasurement'], true);
  }

  // Draw all completed measurements
  variables['b7ef2709-5451-40f1-8cbb-a38ae8211dcc'].forEach(measurement => {
    drawMeasurement(ctx, measurement, false);
  });
};

// Function to draw a single measurement
const drawMeasurement = (ctx, measurement, isActive) => {
  const scale = variables['45cb8d6f-f89f-49f4-a6ac-12d44309c13c'];
  const zoom = variables['da774bda-4044-445f-adde-9c24801e51cb'] / 100;

  // Set styles based on measurement type and active state
  ctx.lineWidth = isActive ? 3 : 2;

  if (measurement.type === 'linear') {
    ctx.strokeStyle = isActive ? '#3b82f6' : '#64748b';

    // Draw the line
    if (measurement.points.length >= 2) {
      ctx.beginPath();
      ctx.moveTo(measurement.points[0].x, measurement.points[0].y);
      ctx.lineTo(measurement.points[1].x, measurement.points[1].y);
      ctx.stroke();

      // Draw start and end points
      ctx.fillStyle = ctx.strokeStyle;
      ctx.beginPath();
      ctx.arc(measurement.points[0].x, measurement.points[0].y, 4, 0, Math.PI * 2);
      ctx.fill();
      ctx.beginPath();
      ctx.arc(measurement.points[1].x, measurement.points[1].y, 4, 0, Math.PI * 2);
      ctx.fill();

      // Draw the measurement value
      const midX = (measurement.points[0].x + measurement.points[1].x) / 2;
      const midY = (measurement.points[0].y + measurement.points[1].y) / 2;

      ctx.fillStyle = '#ffffff';
      ctx.fillRect(midX - 40, midY - 12, 80, 24);
      ctx.strokeRect(midX - 40, midY - 12, 80, 24);

      ctx.fillStyle = '#0f172a';
      ctx.font = '12px Arial';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(`${measurement.value.toFixed(2)} ${scale.unit}`, midX, midY);
    }
  } 
  else if (measurement.type === 'area') {
    ctx.strokeStyle = isActive ? '#3b82f6' : '#64748b';
    ctx.fillStyle = isActive ? 'rgba(59, 130, 246, 0.2)' : 'rgba(100, 116, 139, 0.2)';

    // Draw the polygon
    if (measurement.points.length >= 3) {
      ctx.beginPath();
      ctx.moveTo(measurement.points[0].x, measurement.points[0].y);

      for (let i = 1; i < measurement.points.length; i++) {
        ctx.lineTo(measurement.points[i].x, measurement.points[i].y);
      }

      ctx.closePath();
      ctx.fill();
      ctx.stroke();

      // Draw the measurement value in the center of the polygon
      let centerX = 0;
      let centerY = 0;

      for (let i = 0; i < measurement.points.length; i++) {
        centerX += measurement.points[i].x;
        centerY += measurement.points[i].y;
      }

      centerX /= measurement.points.length;
      centerY /= measurement.points.length;

      ctx.fillStyle = '#ffffff';
      ctx.fillRect(centerX - 50, centerY - 12, 100, 24);
      ctx.strokeRect(centerX - 50, centerY - 12, 100, 24);

      ctx.fillStyle = '#0f172a';
      ctx.font = '12px Arial';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(`${measurement.value.toFixed(2)} sq ${scale.unit}`, centerX, centerY);
    }
  }
  else if (measurement.type === 'count') {
    ctx.fillStyle = isActive ? '#3b82f6' : '#64748b';

    // Draw a marker at the point
    ctx.beginPath();
    ctx.arc(measurement.points[0].x, measurement.points[0].y, 8, 0, Math.PI * 2);
    ctx.fill();

    // Draw the count value
    ctx.fillStyle = '#ffffff';
    ctx.font = '12px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText('1', measurement.points[0].x, measurement.points[0].y);
  }
};

// Function to update the cursor based on the selected tool
const updateCursor = () => {
  const container = wwLib.getFrontDocument().getElementById('drawing_canvas_container');
  if (!container) return;

  const tool = variables['f780e5d4-9fa8-47f5-bcbd-c44c5e28cf51'];

  switch (tool) {
    case 'linear':
      container.style.cursor = 'crosshair';
      break;
    case 'area':
      container.style.cursor = 'crosshair';
      break;
    case 'count':
      container.style.cursor = 'cell';
      break;
    default:
      container.style.cursor = 'default';
  }
};

