// Get the current active measurement
  const measurement = variables['currentMeasurement'];
  if (!measurement || !measurement.isActive) return null;

  const tool = measurement.type;
  const scale = variables['45cb8d6f-f89f-49f4-a6ac-12d44309c13c'].ratio;

  // Update the measurement based on the tool type
  if (tool === 'linear') {
    // For linear measurements, calculate the distance between start and current point
    const dx = context.parameters['currentPoint'].x - measurement.startPoint.x;
    const dy = context.parameters['currentPoint'].y - measurement.startPoint.y;
    const pixelDistance = Math.sqrt(dx * dx + dy * dy);

    // Convert pixel distance to real-world units using the scale
    const realDistance = pixelDistance * scale;

    // Update the measurement
    measurement.endPoint = { ...context.parameters['currentPoint'] };
    measurement.points = [measurement.startPoint, measurement.endPoint];
    measurement.value = realDistance;
  } 
  else if (tool === 'area') {
    // For area measurements, update the polygon points
    // If this is a new point (not just moving the mouse), add it to the points array
    const lastPoint = measurement.points[measurement.points.length - 1];
    if (lastPoint.x !== context.parameters['currentPoint'].x || lastPoint.y !== context.parameters['currentPoint'].y) {
      measurement.points = [...measurement.points, { ...context.parameters['currentPoint'] }];
    }

    // Calculate the area of the polygon
    if (measurement.points.length >= 3) {
      let area = 0;
      for (let i = 0; i < measurement.points.length; i++) {
        const j = (i + 1) % measurement.points.length;
        area += measurement.points[i].x * measurement.points[j].y;
        area -= measurement.points[j].x * measurement.points[i].y;
      }
      area = Math.abs(area / 2);

      // Convert pixel area to real-world units using the scale
      const realArea = area * scale * scale;
      measurement.value = realArea;
    }
  }
  else if (tool === 'count') {
    // For count measurements, just increment the counter
    measurement.points = [{ ...context.parameters['currentPoint'] }];
    measurement.value = 1;
  }

  // Update the current measurement
  variables['currentMeasurement'] = measurement;

  return measurement;

