if (mode === 'create') {
  // Generate a new ID
  const newId = 'INV' + (Math.floor(Math.random() * 900) + 100);

  // Create new invoice object
  const newInvoice = {
    id: newId,
    number: 'INV-' + new Date().getFullYear() + '-' + (Math.floor(Math.random() * 900) + 100),
    status: formData.status || 'Pending',
    clientId: formData.clientId,
