    dueDate: formData.dueDate,
    amount: context.component?.formulas?.['d28a7df4-3eea-428f-b1fb-9098b2cb05a6'](),
    paid: 0,
    balance: context.component?.formulas?.['d28a7df4-3eea-428f-b1fb-9098b2cb05a6']()
  };

  // Add to invoices data
  invoicesData.push(newInvoice);
} else if (mode === 'edit') {
  // Find the invoice to update
  const index = invoicesData.findIndex(invoice => invoice.id === invoiceId);

  if (index !== -1) {
    // Update the invoice
    invoicesData[index] = {
      ...invoicesData[index],
      status: formData.status || invoicesData[index].status,
